def parse_cofog_data(cofog1_line, cofog2_line, cofog3_line):
    cofog1_list = cofog1_line.split("^")
    cofog2_list = cofog2_line.split("^")
    cofog3_list = cofog3_line.split("^")

    for cofog1 in cofog1_list:
        cofog1_code, cofog1_name = cofog1.split(" ", 1)
        print(cofog1_code, cofog1_name)

        cofog2_sublist = []
        for cofog2 in cofog2_list:
            cofog2_code, cofog2_name = cofog2.split(" ", 1)
            if cofog2_code.startswith(cofog1_code):
                print("\t", cofog2_code, cofog2_name)

                cofog3_sublist = []
                for cofog3 in cofog3_list:
                    cofog3_code, cofog3_name = cofog3.split(" ", 1)
                    if cofog3_code.startswith(cofog2_code):
                        print("\t\t", cofog3_code, cofog3_name)
                        c3 = Cofog3(code=cofog3_code.strip(), name=cofog3_name.strip())
                        cofog3_sublist.append(c3)

                c2 = Cofog2(code=cofog2_code.strip(), name=cofog2_name.strip(), cofog3=cofog3_sublist)
                cofog2_sublist.append(c2)

        c1 = Cofog1(code=cofog1_code.strip()[:-1], name=cofog1_name.strip(), cofog2=cofog2_sublist)
        c1.save()


db = get_db("psped")
db.drop_collection("cofog")
parse_cofog_data(cofog1, cofog2, cofog3)
